function isTimer() {
  const minutesDeadline = new Date(2022, 05, 19, 21, 19);

  let timerDays = document.querySelectorAll('[data-timer-day]');
  let timerHours = document.querySelectorAll('[data-timer-hour]');
  let timerMinuts = document.querySelectorAll('[data-timer-min]');
  let timerSeconds = document.querySelectorAll('[data-timer-sec]');

  function countdownTimer() {
    const diff = minutesDeadline - new Date();
    console.log(diff);
    if (diff <= 0) {
      clearInterval(timerId);
      let timerBtns = document.querySelectorAll('*[data-timer-btn]');
      for (let timerBtn of timerBtns) {
        timerBtn.classList.remove('btn__wrapper-blocked');
      }
    }

    const days = diff > 0 ? Math.floor(diff / 1000 / 60 / 60 / 24) : 0;
    const hours = diff > 0 ? Math.floor(diff / 1000 / 60 / 60) % 24 : 0;
    const minutes = diff > 0 ? Math.floor(diff / 1000 / 60) % 60 : 0;
    const seconds = diff > 0 ? Math.floor(diff / 1000) % 60 : 0;

    let dayscString = days < 10 ? '0' + days : String(days);
    let hoursString = hours < 10 ? '0' + hours : String(hours);
    let minutesString = minutes < 10 ? '0' + minutes : String(minutes);
    let secondsString = seconds < 10 ? '0' + seconds : String(seconds);

    for (let timerDay of timerDays) {
      timerDay.innerHTML = dayscString;
    }
    for (let timerHour of timerHours) {
      timerHour.innerHTML = hoursString;
    }
    for (let timerMinut of timerMinuts) {
      timerMinut.innerHTML = minutesString;
    }
    for (let timerSecond of timerSeconds) {
      timerSecond.innerHTML = secondsString;
    }
  }

  // вызываем функцию countdownTimer
  countdownTimer();
  // вызываем функцию countdownTimer каждую секунду
  let timerId = setInterval(countdownTimer, 1000);
}

isTimer();
